//wap to display week days based on given input 
package qsp;

public class p28 {
    //5.switch
	public static void main(String[] args) {
		// TODO Auto-generated method stub
     char name='W';
     switch (name) {
	        case 'M':System.out.println("Monday");
		    break;
            
	        case 'T':System.out.println("Tuesday");
		    break;
		    
	        case 'W':System.out.println("Wednesday");
		    break;
		    
	        case 'H':System.out.println("Thursday");
		    break;
		    
	        case 'F':System.out.println("Friday");
		    break;
		    
	        case 'S':System.out.println("Saturday");
		    break;
		   
	        default:System.out.println("Sunday");
		    break;
	}
	}

}
