package qsp;

public class p29 {
    //5.switch
	public static void main(String[] args) {
		// TODO Auto-generated method stub
     char n='a';
     switch (n) {
	    case 97:System.out.println("Hindi");
		break;
        
	    case 2:System.out.println("Marathi");
	    break;
	    
	    default:System.out.println("Invalid");
		break;
	}
	}
//NOTE:ASCII number of character a is 97 that is why we are getting output in above Programme as Hindi
}
