//wap to display number from 10 to 1 and "hii" using do while loop
package qsp;

public class p34 {
	//2.do while loop
	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int a=10;
        do {
			System.out.println(a);
			a--;
		} while (a>=1);
        System.out.println("hii");
	}

}
